// Example fix for src/pages/PostJobPage.js navigation with react-router-dom v6's useNavigate
import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

export default function PostJobPage() {
  const navigate = useNavigate();
  const [job, setJob] = useState({
    jobTitle: '',
    company: '',
    location: '',
    description: '',
    requirements: '',
    salaryMin: '',
    salaryMax: '',
    industry: '',
    skills: '',
  });

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      await api.post('/jobs', {
        jobTitle: job.jobTitle,
        company: job.company,
        location: job.location,
        description: job.description,
        requirements: job.requirements.split(',').map((r) => r.trim()),
        salary: { min: Number(job.salaryMin), max: Number(job.salaryMax) },
        industry: job.industry,
        skills: job.skills.split(',').map((s) => s.trim()),
      });
      alert('Job posted successfully');
      navigate('/', { replace: true });
    } catch (err) {
      alert('Failed to post job');
    }
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: 600 }}>
        {/* inputs similar to previous code */}
        <label>Job Title:</label>
        <input
          type="text"
          required
          value={job.jobTitle}
          onChange={(e) => setJob({ ...job, jobTitle: e.target.value })}
          style={{ width: '100%', marginBottom: 10, padding: 8 }}
        />
        {/* ...other inputs here */}
        <label>Company:</label>
        <input
          type="text"
          required
          value={job.company}
          onChange={(e) => setJob({ ...job, company: e.target.value })}
          style={{ width: '100%', marginBottom: 10, padding: 8 }}
        />
        {/* Continue rest as before... */}
        <button type="submit" style={{ padding: 10 }}>
          Post Job
        </button>
      </form>
    </div>
  );
}
